INSERT INTO `creature_ai_texts` VALUES (-1, 'I see those fools at the Abbey sent some fresh meat for us.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '103');
INSERT INTO `creature_ai_texts` VALUES (-2, 'The Brotherhood will not tolerate your actions.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Defais Text');
INSERT INTO `creature_ai_texts` VALUES (-3, 'Ah, a chance to use this freshly sharpened blade.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Defais Text');
INSERT INTO `creature_ai_texts` VALUES (-4, 'Feel the power of the Brotherhood!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Defais Text');
INSERT INTO `creature_ai_texts` VALUES (-5, 'More bones to gnaw on...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Gnoll Text');
INSERT INTO `creature_ai_texts` VALUES (-6, 'Grrrr... fresh meat!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Gnoll Text');
INSERT INTO `creature_ai_texts` VALUES (-7, 'You no take candle!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Kobold Text');
INSERT INTO `creature_ai_texts` VALUES (-8, 'Yiieeeee! Me run!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Kobold Text');
INSERT INTO `creature_ai_texts` VALUES (-9, 'What aggravation is this? You will die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11205, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-10, 'Scream while you burn! ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11211, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-11, 'I\'ll incinerate you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11210, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-12, 'I revel in your pain!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11209, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-13, 'You seek a prize, eh? How about death?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11206, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-14, 'Your life will be mine!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11208, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-15, 'I hate to say I told you so...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11207, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-16, 'You won\'t... get far.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11212, 1, 0, 0, '17978');
INSERT INTO `creature_ai_texts` VALUES (-17, 'What are you doing? These specimens are very delicate!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11144, 1, 0, 0, '17975');
INSERT INTO `creature_ai_texts` VALUES (-18, 'Nature bends to my will!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11148, 1, 0, 0, '17975');
INSERT INTO `creature_ai_texts` VALUES (-19, 'Endorel aluminor!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11147, 1, 0, 0, '17975');
INSERT INTO `creature_ai_texts` VALUES (-20, 'Your lifecycle is now concluded!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11145, 1, 0, 0, '17975');
INSERT INTO `creature_ai_texts` VALUES (-21, 'The specimens... must be... preserved.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11149, 1, 0, 0, '17975');
INSERT INTO `creature_ai_texts` VALUES (-22, 'VanCleef pay big for your heads!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5774, 1, 0, 0, '644');
INSERT INTO `creature_ai_texts` VALUES (-23, 'On guard!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11241, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-24, 'Defend yourself, for all the good it will do...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11242, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-25, 'Knew this was... the only way out', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11243, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-26, 'Yes, that was quite satisfying', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11239, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-27, 'At last, a target for my frustrations!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11238, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-28, 'Have you come to kill Dalliah? Can I watch?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11237, 1, 0, 0, '20886');
INSERT INTO `creature_ai_texts` VALUES (-29, 'Life energy to... consume.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11250, 1, 0, 0, '20870');
INSERT INTO `creature_ai_texts` VALUES (-30, 'The shadow... will engulf you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11253, 1, 0, 0, '20870');
INSERT INTO `creature_ai_texts` VALUES (-31, 'This vessel... is empty.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11251, 1, 0, 0, '20870');
INSERT INTO `creature_ai_texts` VALUES (-32, 'No...more...life.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11252, 1, 0, 0, '20870');
INSERT INTO `creature_ai_texts` VALUES (-33, 'The void...beckons.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11255, 1, 0, 0, '20870');
INSERT INTO `creature_ai_texts` VALUES (-34, 'Step forward! I will see that you are properly welcomed.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11071, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-35, 'Oh stop your whimpering.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11072, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-36, 'I have not yet... begun to...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11079, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-37, 'You are no longer dealing with some underling.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11076, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-38, 'Band\'or shorel\'aran!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11077, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-39, 'Guards! Come and kill these intruders!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11078, 1, 0, 0, '17976');
INSERT INTO `creature_ai_texts` VALUES (-40, 'Is this the end?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '15551');
INSERT INTO `creature_ai_texts` VALUES (-41, 'What will become of...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '15551');
INSERT INTO `creature_ai_texts` VALUES (-42, 'Time to join us, $C.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1157');
