INSERT INTO `creature_ai_texts` VALUES (-195, 'Brzt... click... whir...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2922');
INSERT INTO `creature_ai_texts` VALUES (-196, 'It is dangerous here, master. But I shall help stand guard.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2922');
INSERT INTO `creature_ai_texts` VALUES (-197, 'We can make it better, stronger, faster. We have the technology. We have the magic.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2922');
