INSERT INTO `creature_ai_texts` VALUES (-216, 'All of your efforts have been in vain, for the draining of the World Tree has already begun! Soon, the heart of your world shall beat no more!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10986, 1, 0, 0, '13083');
INSERT INTO `creature_ai_texts` VALUES (-217, 'You are defenders of a doomed world! Flee here, and perhaps you will prolong your pathetic lives!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10977, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-218, 'Your hopes are lost!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10981, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-219, 'Scream for me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11038, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-220, 'Pity, no time for a slow death!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11039, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-221, 'The clock... is still... ticking.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10982, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-222, 'The swarm is eager to feed!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10979, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-223, 'Pestilence upon you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11037, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-224, 'You look tired...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10978, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-225, 'Sweet dreams...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11545, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-226, 'Let fire rain from above!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10980, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-227, 'Earth and sky shall burn!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11036, 1, 0, 0, '17808');
INSERT INTO `creature_ai_texts` VALUES (-228, 'I gonna make you into mojo!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Frostmane Troll Text');
INSERT INTO `creature_ai_texts` VALUES (-229, 'Killing you be easy.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Frostmane Troll Text');
