INSERT INTO `creature_ai_texts` VALUES (-231, 'I\'ll cut you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Frostmane Troll Text');
INSERT INTO `creature_ai_texts` VALUES (-232, 'The Brotherhood will not be hampered by insects.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '6846');
INSERT INTO `creature_ai_texts` VALUES (-233, 'So much to do, so much to do! Where does the time go?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '295');
INSERT INTO `creature_ai_texts` VALUES (-234, 'Furious winds envelop Vakkiz as he writhes menacingly towards you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '23162');
INSERT INTO `creature_ai_texts` VALUES (-235, '%s\'s flesh shatters in an unearthly rage!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '23162');
INSERT INTO `creature_ai_texts` VALUES (-236, '%s bares her fangs and hisses menacingly at those around her.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '23163');
INSERT INTO `creature_ai_texts` VALUES (-237, '%s focuses on $N.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '23163');
INSERT INTO `creature_ai_texts` VALUES (-238, 'Fool! The essence of the hawk will never be yours!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22992');
INSERT INTO `creature_ai_texts` VALUES (-239, 'The time of the Raven is night, but yours will end here!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22992');
INSERT INTO `creature_ai_texts` VALUES (-240, 'You dare to disturb the loyal followers of the Raven?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22993');
INSERT INTO `creature_ai_texts` VALUES (-241, 'You will die for your hubris, druid!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22993');
INSERT INTO `creature_ai_texts` VALUES (-242, 'You dare to disturb the loyal followers of the Raven?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22994');
INSERT INTO `creature_ai_texts` VALUES (-243, 'You will die for your hubris, druid!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22994');
INSERT INTO `creature_ai_texts` VALUES (-244, 'Someone come read this wanted poster to Grek. Grek can\'t read.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19606');
INSERT INTO `creature_ai_texts` VALUES (-245, 'Grek try!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19606');
INSERT INTO `creature_ai_texts` VALUES (-246, 'This oil no good for Grek! What Grek look like to you, some weakling in robes?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19606');
INSERT INTO `creature_ai_texts` VALUES (-247, 'Grek get a drink.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19606');
INSERT INTO `creature_ai_texts` VALUES (-248, 'I never thought it would turn out to be mana oil! There\'s going to be plenty of demand for that, minus Grek of course!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18385');
INSERT INTO `creature_ai_texts` VALUES (-249, 'Ship it!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18385');
INSERT INTO `creature_ai_texts` VALUES (-250, 'Learning to read is a thirsty business. A toast, eh?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '21193');
INSERT INTO `creature_ai_texts` VALUES (-251, '%s faints.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '18705');
INSERT INTO `creature_ai_texts` VALUES (-252, 'C\'mere, $r. Ya don\'t want to be riskin\' yerself in a dungeon, do ya? Filthy work, and for what? Some worthless trinket? Nah, what ya need be one of my amulets. Guarenteed to work! ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19227');
INSERT INTO `creature_ai_texts` VALUES (-253, 'Dangerous out there, innit? Know what ya need? Protection. I can help ya defy death itself! Ya wear this madstone \'round yer neck, see, and then ya be able to come back if ya find yerself in the spirit world! Just a quick jaunt to yer corpse and up ya com', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19227');
INSERT INTO `creature_ai_texts` VALUES (-254, 'Amazing Amulets! Incredible curios! The newfangled jewlcrafters be havin\' nothin\' on the tried and true mystical methods of ol\' Griftah! Improve yerself through these magical talismans for a bargain price!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19227');
INSERT INTO `creature_ai_texts` VALUES (-255, 'I could have sworn I had more apples. Grok!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19223');
INSERT INTO `creature_ai_texts` VALUES (-256, 'If no one help. Grok smash!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22940');
INSERT INTO `creature_ai_texts` VALUES (-257, 'Look over here, $c! I got somethin\' for ya. Just what ya need. It\'s soap, right... and it\'s on a rope! No offense, man, but yer a little ripe, you know what I\'m sayin\'? ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19227');
INSERT INTO `creature_ai_texts` VALUES (-258, 'Hey! Hey, $r! I be havin\' just the thing for ya. Ya ever see a Tikbalang? Frightenin\' creatures. Prone to stealin\' ya away. What ya need is my tikbaland wards. Guaranteed to ward away all tikbalangs!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19227');
INSERT INTO `creature_ai_texts` VALUES (-259, 'Grok need help! Why no one help?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22940');
INSERT INTO `creature_ai_texts` VALUES (-260, 'Mog\'dorg the Wizened tell Grok go to city, get help for Bloodmaul. Grok go.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22940');
INSERT INTO `creature_ai_texts` VALUES (-261, 'Bladespire clan not fight fair. Have Gronn to help dem. You go to Blade\'s Edge Mountains. Talk to Mog\'dorg the Wizened. Kill Gromm.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22940');
INSERT INTO `creature_ai_texts` VALUES (-262, 'Ya look tired, $c. I be havin\' somethin\' for ya. These amulets, they can help ya leap obstacles and swim through the rivers like ya be born to it. They energize the food ya be eatin\'. Makin\' yer wounds close before yer eyes!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19227');
INSERT INTO `creature_ai_texts` VALUES (-263, 'What is that poor orc yelling about? Someone should see what is going on.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19223');
INSERT INTO `creature_ai_texts` VALUES (-264, 'I feel that war is fast approaching. The fruit vendor has enlisted to help an ogre. I shall have to enlist help as well.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19196');
INSERT INTO `creature_ai_texts` VALUES (-265, 'Ya look lovesick. Special someone be gettin\' ya down, eh? No worries, no worries. Ya get this medallion from me, ya wear it when ya see \'em and they be all over ya, sweetheart!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19227');
INSERT INTO `creature_ai_texts` VALUES (-266, 'Me hungry. Fruit look tasty.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22940');
INSERT INTO `creature_ai_texts` VALUES (-267, 'FRUIT VENDOR!!! Your cart is still in our way! We will give you one more hour to move it from our area. Do not test our patience anymore!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '19196');
INSERT INTO `creature_ai_texts` VALUES (-268, 'Grok get angry if puny ones not go to Blade\'s Edge and talk to Mog\'dorg!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22940');
INSERT INTO `creature_ai_texts` VALUES (-269, 'Nothing like a tankard full to wash down one of my magnificent muffins.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19664');
INSERT INTO `creature_ai_texts` VALUES (-270, 'Yea, that hit the spot. Muffins for sale! Get your fresh muffins right here!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19664');
INSERT INTO `creature_ai_texts` VALUES (-271, 'Yea, yea, I\'m the muffin man. You know why? Because I sell muffins, that\'s why!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19664');
INSERT INTO `creature_ai_texts` VALUES (-272, 'I\'ve got muffins right over here. Come on over!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19664');
INSERT INTO `creature_ai_texts` VALUES (-273, 'May it be so.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19138');
INSERT INTO `creature_ai_texts` VALUES (-274, 'Anchorite Nindumen, I have a request to make of you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19346');
INSERT INTO `creature_ai_texts` VALUES (-275, 'We\'ve just sent another inexperienced squad into Nagrand. Might you offer a prayer for them?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19346');
INSERT INTO `creature_ai_texts` VALUES (-276, 'May it be so.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19346');
INSERT INTO `creature_ai_texts` VALUES (-277, 'Of course, my friend. How can I be of service to you?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-278, 'A noble request. It would be an honor, Erothem.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-279, 'My friends, please join me in humble supplication the the Light.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-280, 'Light that we embrace, we beseech thee...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-281, 'Into our struggle against the darkness we have sent our children.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-282, 'Please bless and protect them, and grant them success over those who seek to do us harm...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-283, 'Continue to bless this, our ancient home, we beg.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-284, 'Bless us with tolerance for our elven visitors.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-285, 'By tha Naaru, may it be so.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19378');
INSERT INTO `creature_ai_texts` VALUES (-286, '%s nods.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '16589');
INSERT INTO `creature_ai_texts` VALUES (-287, 'Lost some of my best soldiers that day...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16589');
INSERT INTO `creature_ai_texts` VALUES (-288, 'But that\'s not the worst of it. Before I could pull us back, a group of \'em spotted us and attacked.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16589');
INSERT INTO `creature_ai_texts` VALUES (-289, 'I dunno, grunt. I just - don\'t - know...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16589');
INSERT INTO `creature_ai_texts` VALUES (-290, 'Fel orcs...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16589');
INSERT INTO `creature_ai_texts` VALUES (-291, 'Big red bastards! Bigger than any orc I\'d ever seen. Muscles out to here, tusks out to here...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16589');
INSERT INTO `creature_ai_texts` VALUES (-292, 'Sure enough, scount came runnin\' back, talkin\' bout some orcs down the path. They hadn\'t seen us yet so we figured we\'d go get aquainted.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16589');
INSERT INTO `creature_ai_texts` VALUES (-293, 'So there we were - me and the battalion, doing a routine recon down the bone road. We\'d sent the scout up ahead to look around in case anything dangerous come up.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16589');
INSERT INTO `creature_ai_texts` VALUES (-294, 'Do I detect a hint of cinnamon? Or is that nutmeg? Whatever it is, one thing is for sure - nobody cooks donkey like you, Cookie.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16590');
INSERT INTO `creature_ai_texts` VALUES (-295, 'If my stomach wasn\'t threatening to dislodge itself from my body and leave me for the refuse that we give to the prisoners, I might be insulted by this... this food.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16590');
INSERT INTO `creature_ai_texts` VALUES (-296, 'And if I hear that you palookas complained to Nazgrel again, it\'ll be one of ya\'s that I serve up next time...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16585');
INSERT INTO `creature_ai_texts` VALUES (-297, 'Yer slop is ready. Come n\' get it...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16585');
INSERT INTO `creature_ai_texts` VALUES (-298, 'I\'ve seen gnomes work harder than you, peon! Little girl gnomes - with pig tails! MOVE IT, MOVE IT, MOVE IT!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16584');
INSERT INTO `creature_ai_texts` VALUES (-299, 'What kinda orcs, chief?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16590');
INSERT INTO `creature_ai_texts` VALUES (-300, 'You mean...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16590');
INSERT INTO `creature_ai_texts` VALUES (-301, 'But how, chief? I thought the curse was lifted...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16590');
INSERT INTO `creature_ai_texts` VALUES (-302, 'The warchief\'s put a price on Arazzius\'s head! As of right now, that no-good piece of demon refuse is enemy number one.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19255');
INSERT INTO `creature_ai_texts` VALUES (-303, 'Yea, we got problems. I think that\'s crystal clear. The question is, what are we gonna do about it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19255');
INSERT INTO `creature_ai_texts` VALUES (-304, 'What is it that you want from me, shaman?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '21769');
INSERT INTO `creature_ai_texts` VALUES (-305, 'Do you not have eyes? Did you not see the demons on our very doorstep? I do not have the soldiers to spare!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '21769');
INSERT INTO `creature_ai_texts` VALUES (-306, 'Pray to your gods, shaman. Perhaps they will come to your aid. The Horde however, cannot...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '21769');
INSERT INTO `creature_ai_texts` VALUES (-307, 'Wow! Look at his muscles!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18296');
INSERT INTO `creature_ai_texts` VALUES (-308, 'Will he win? Can he win?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18292');
INSERT INTO `creature_ai_texts` VALUES (-309, 'He\'s so well diciplined!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18293');
INSERT INTO `creature_ai_texts` VALUES (-310, 'I wanna be just like the pitfighter!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18292');
INSERT INTO `creature_ai_texts` VALUES (-311, 'This one is slightly better than the last. However, it still suffers from the same flimsy bone structure as the others. When you summon one of these, you are on the right path.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '11582');
INSERT INTO `creature_ai_texts` VALUES (-312, 'How does he do it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18296');
INSERT INTO `creature_ai_texts` VALUES (-313, 'That\'s the best reward we\'re going to be able to offer. Hopefully someone accepts the mission.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18407');
INSERT INTO `creature_ai_texts` VALUES (-314, 'Damm those ogres. Damn them to the Nether. Won\'t someone save us from these savages! This is the fifth kidnapping is as many days.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18407');
INSERT INTO `creature_ai_texts` VALUES (-315, 'Any time any of you peons want to take a break, the graveyard is right over there.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19362');
INSERT INTO `creature_ai_texts` VALUES (-316, 'If I see any lazy peons here, I\'ll load them into a catapult and launch them off to the Black Temple!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '19362');
INSERT INTO `creature_ai_texts` VALUES (-317, 'Wit a reward like dis, how could they refuse, mon?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '21771');
INSERT INTO `creature_ai_texts` VALUES (-318, 'Last time someone went out to try and collect the bounty on Netherlock, all we got back was a compressed layer of foolhardy adventurer!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19541');
INSERT INTO `creature_ai_texts` VALUES (-319, 'Maybe I should gather up some other agents and head out there after this shift?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19541');
INSERT INTO `creature_ai_texts` VALUES (-320, 'Sands consume you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5872, 1, 0, 0, '7271');
INSERT INTO `creature_ai_texts` VALUES (-321, 'Iman m t\'ief fu Fus\'obeah italaf', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Frostmane Troll Text');
INSERT INTO `creature_ai_texts` VALUES (-322, 'Dim wha Siame cyaa fi so yudo', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Frostmane Troll Text');
INSERT INTO `creature_ai_texts` VALUES (-323, 'So ju weh siame is fus nehjo skam worl Uptfeel', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1260');
INSERT INTO `creature_ai_texts` VALUES (-324, 'Time to bash!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1119');
INSERT INTO `creature_ai_texts` VALUES (-325, 'Er... \'Moke?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19395');
INSERT INTO `creature_ai_texts` VALUES (-326, 'I\'m... not sure that\'s a good idea, \'Moke. Ye might get hurt...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19395');
INSERT INTO `creature_ai_texts` VALUES (-327, 'What, with me fists?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19395');
INSERT INTO `creature_ai_texts` VALUES (-328, '%s blinks, a perplexed expression crossing his face.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '19395');
INSERT INTO `creature_ai_texts` VALUES (-329, '...THAT WAS GREAT!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19394');
INSERT INTO `creature_ai_texts` VALUES (-330, 'DO IT, YE PANSEY!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19394');
INSERT INTO `creature_ai_texts` VALUES (-331, 'Nae, that\'s fer wimps. Hit me with yer hammer!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19394');
INSERT INTO `creature_ai_texts` VALUES (-332, 'Bron! \'Ey Bron! \'Ey Bron! Hit me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19394');
INSERT INTO `creature_ai_texts` VALUES (-333, 'I don\'t get it! Are ya blind? Can ya not see the demons bangin\' on our door? Who am I gonna send out to talk to your shaman friends? Point \'em out!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '21773');
INSERT INTO `creature_ai_texts` VALUES (-334, 'Do you think that demons will play fair? These beasts are more savage than the orcs, more cunning than the undead. Do not attempt to engage Legion by normal standards of war.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19308');
INSERT INTO `creature_ai_texts` VALUES (-335, 'How many more soldiers need to die before we start paying attention out there?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19308');
INSERT INTO `creature_ai_texts` VALUES (-336, 'This war - and don\'t mistake this for anything but war - is not going to win itself. If we do not fight, scratch, claw, and kick for every speck of land on this shattered world, all will be lost.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19308');
INSERT INTO `creature_ai_texts` VALUES (-337, 'The creatures of this place are strong, wayfarer. You\'ll need ample ammunition if you hope to survive. You\'re in luck - I am well-stocked with fine arrows, bullets, and throwing weapons. The price is reasonable, considering the circumstances.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19534');
INSERT INTO `creature_ai_texts` VALUES (-338, 'Ahh, another powerful traveler. The nether can be dangerous; you\'ll want the most potent spells at your disposal. I carry the finest reagents, common and exotic. Come and buy - you won\'t be sorry!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '19535');
INSERT INTO `creature_ai_texts` VALUES (-339, '%s whispers softle in reverent tones under her breath.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '21066');
INSERT INTO `creature_ai_texts` VALUES (-340, '%s examines the information on the wanted poster.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '18098');
INSERT INTO `creature_ai_texts` VALUES (-341, '%s jumps out of the shadows!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '636');
INSERT INTO `creature_ai_texts` VALUES (-342, '%s calls more of his allies out of the shadows.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '639');
INSERT INTO `creature_ai_texts` VALUES (-343, 'Let me help you with those.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '5042');
INSERT INTO `creature_ai_texts` VALUES (-344, 'Drink this, it will help.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '5042');
INSERT INTO `creature_ai_texts` VALUES (-345, 'You\'re going to be just fine.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '5042');
INSERT INTO `creature_ai_texts` VALUES (-346, '%s begins to summon in reinforcements!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '2358');
INSERT INTO `creature_ai_texts` VALUES (-347, 'The Helmsman comes for you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '32576');
INSERT INTO `creature_ai_texts` VALUES (-348, 'Now you face Orabus, fool!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '32576');
INSERT INTO `creature_ai_texts` VALUES (-349, 'Where is that dog?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11796, 0, 0, 0, '23618');
INSERT INTO `creature_ai_texts` VALUES (-350, 'How dare you disturb the master\'s dinner!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '16414');
INSERT INTO `creature_ai_texts` VALUES (-351, 'That was wasted on the likes of you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16414');
INSERT INTO `creature_ai_texts` VALUES (-352, 'That was a 200 year old Pupellyverbos Port!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16414');
INSERT INTO `creature_ai_texts` VALUES (-353, 'Me no run from $C like you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Trogg Common Text');
INSERT INTO `creature_ai_texts` VALUES (-354, 'Yip! Me kill!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Trogg Common Text');
INSERT INTO `creature_ai_texts` VALUES (-355, 'The only good $R is a dead $R!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1205');
INSERT INTO `creature_ai_texts` VALUES (-356, 'Weak $C! You are no match for the Stonesplinter Tribe!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1205 and 1399');
INSERT INTO `creature_ai_texts` VALUES (-357, 'Feel the power of the Dark Iron Dwarves!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1169');
INSERT INTO `creature_ai_texts` VALUES (-358, 'Time to die, $C!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1169');
INSERT INTO `creature_ai_texts` VALUES (-359, 'I\'ll crush you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Mo\'grosh Common Text');
INSERT INTO `creature_ai_texts` VALUES (-360, 'Me smash! You die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Mo\'grosh Common Text');
INSERT INTO `creature_ai_texts` VALUES (-361, 'Raaar!!! Me smash $R!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Mo\'grosh Common Text');
INSERT INTO `creature_ai_texts` VALUES (-362, 'King Magni Bronzebeard is a fool and a charlatan!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1222');
INSERT INTO `creature_ai_texts` VALUES (-363, 'The Thandol Span fell to Ragnaros. So shall the Stonewrought Dam!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1222');
INSERT INTO `creature_ai_texts` VALUES (-364, 'Wahehe! I\'m taking you down with me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1222');
INSERT INTO `creature_ai_texts` VALUES (-365, 'I tire of this nonsense! Prepare to die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2318');
INSERT INTO `creature_ai_texts` VALUES (-366, 'ARRRRRRR!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '13602');
INSERT INTO `creature_ai_texts` VALUES (-367, 'Huh? What dat?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1210');
INSERT INTO `creature_ai_texts` VALUES (-368, 'Dat $R look gud to eat!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1210');
INSERT INTO `creature_ai_texts` VALUES (-369, 'Bash it!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1210');
INSERT INTO `creature_ai_texts` VALUES (-370, 'A $C called $N? You\'ll make a fine breakfast!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1205');
INSERT INTO `creature_ai_texts` VALUES (-371, 'Die $R! These lands belong to the Stonesplinter Tribe!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1399');
INSERT INTO `creature_ai_texts` VALUES (-372, 'The Loch belongs to the Stonesplinter Tribe now, $N! Now die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1399');
INSERT INTO `creature_ai_texts` VALUES (-373, 'Die in the name of Ragnaros!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1222');
INSERT INTO `creature_ai_texts` VALUES (-374, '[Dwarvish] Dum mos', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2149');
