INSERT INTO `creature_ai_texts` VALUES (-376, '[Orcish] Ogg kaz ko zugas kil mog Tov\'nokaz Kagg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Orc Common Text');
INSERT INTO `creature_ai_texts` VALUES (-377, '[Orcish] Tago mog regas gi mog Tov\'nokaz Kagg', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Orc Common Text');
INSERT INTO `creature_ai_texts` VALUES (-378, 'Berserker stance! Attack them recklessly!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '23049');
INSERT INTO `creature_ai_texts` VALUES (-379, 'Mine! You shall not take this place!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '24960');
INSERT INTO `creature_ai_texts` VALUES (-380, 'Get away, this place is ours!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '24966');
INSERT INTO `creature_ai_texts` VALUES (-381, 'ATTENTION!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-382, 'Alright, quiet down. We have a lot of work to do and very little time to do it in, people.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-383, 'I\'ve stood with some of you draenei before at Skettis, and others in Shadowmoon Valley. But now we\'re on Azeroth, and the situation looks grim.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-384, 'First though, you sin\'dorei listen up. I run a tight ship... this isn\'t going to be like what you\'re used to with the Scryers. Pull your weight, or get back on the boat!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-385, 'Now that that\'s out of the way, welcome to the Isle of Quel\'Danas. The Sunwell\'s on the plateau to the south, and Prince Kael\'thas is holed up in the Magisters\' Terrace to the east.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-386, 'We\'re here as support for the so-called \'adventurers\' that the brass called in. We\'ll help them hold the key points on the island, and keep the heat off so that they can do their jobs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-387, 'If none of you ladies have any questions, you can get further instruction and assignments from your squad leaders. That\'s it. Good luck!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-388, 'Hearty Kill!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5801, 1, 0, 0, '4831');
INSERT INTO `creature_ai_texts` VALUES (-389, 'Welcome to the Sunwell. Despite the mild temperatures and scenic vistas, you\'re not on vacation, ladies and gentlemen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-390, 'The current situation looks like this: we\'re holding what we can while those with more experience and better equipment are moving against Prince Kael\'thas.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-391, 'I know, we thought he was dead too, but that\'s not the case. He\'s trying to use the Sunwell to bring the master of the Burning Legion, Kil\'jaeden, into this world.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-392, 'Let me assure you people that if he\'s successful, this world will suffer far worse than Outland ever did! So let\'s make sure that doesn\'t come to pass.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-393, 'Warriors will be deployed to hold key positions, while you marksmen will be assigned to keep the enemy fliers at bay.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-394, 'I\'m awaiting orders on your specific assignments. In the meantime, stay here and be ready to move at a moment\'s notice!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-395, 'Hello to you new recruits. I\'m Drill Sergeant Bahduum. For the next few days, you\'re going to receive orientation.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-396, 'The Isle of Quel\'Danas has been overrun by the forces of Prince Kael\'thas Sunstrider. Intel had him pegged as deceased. Intel was wrong.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-397, 'Up on the plateau, he and his demon friends are trying to reignite the Sunwell and open a rift to bring their head-honcho, Kil\'Jaeden back into the world.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-398, 'Our job is to clear out as much of the fodder as we can, while supporting the specialists that the powers-that-be are having brought in.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-399, 'Needless to say, a lot of us won\'t be going home. But if you remember your training and keep your wits about you, you\'ll greatly increase your chances.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-400, 'That\'s all I have to say for now. Stay focused, boys and girls.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
INSERT INTO `creature_ai_texts` VALUES (-401, 'Shattered Sun scum! Fire at will!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25132');
INSERT INTO `creature_ai_texts` VALUES (-402, 'Keep your eye on the demons. We\'re not concerned with killing Scourge today.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25144');
INSERT INTO `creature_ai_texts` VALUES (-403, 'Dragonhawk incoming from the west! Shoot that druid down!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25144');
INSERT INTO `creature_ai_texts` VALUES (-404, 'I cannot permit you to enter the temple! I must destroy you for your own good!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Green Dragon Text');
INSERT INTO `creature_ai_texts` VALUES (-405, '%s seems much calmer now that it has eaten the remains of $N.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '2931');
INSERT INTO `creature_ai_texts` VALUES (-406, 'Never cross a Dark Iron, $C.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1169');
INSERT INTO `creature_ai_texts` VALUES (-407, 'I shall spill your blood, $C!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '315');
INSERT INTO `creature_ai_texts` VALUES (-408, 'Anyone want to take a break?  Well too bad!  Get to work you oafs!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1763');
INSERT INTO `creature_ai_texts` VALUES (-409, 'Get those parts moving down to the ship!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1763');
INSERT INTO `creature_ai_texts` VALUES (-410, 'Hmmm, this one looks like something that would be better off as a windchime. Take notes class... This is NOT what you want to summon in the heat of battle.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '11582');
INSERT INTO `creature_ai_texts` VALUES (-411, 'I will gladly pay you Tuesday for a hamburger today.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1395');
INSERT INTO `creature_ai_texts` VALUES (-412, 'Spare some change for a poor blind man?...What do you mean im not blind?...I\'M NOT BLIND! I CAN SEE! ITS A MIRACLE!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1395');
INSERT INTO `creature_ai_texts` VALUES (-413, 'It\'s all their fault, stupid Alliance army. Just had to build their towers right behind my farm.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1395');
INSERT INTO `creature_ai_texts` VALUES (-414, 'If the Captain finds out, it\'ll be the end of me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1719');
INSERT INTO `creature_ai_texts` VALUES (-415, 'They must have had someone helping them.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1719');
INSERT INTO `creature_ai_texts` VALUES (-416, 'All of a sudden they were everywhere.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1719');
INSERT INTO `creature_ai_texts` VALUES (-417, 'Sir, our customers are complaining that there\'s not enough Maiden\'s Anguish in our Deadly Poisons.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28355');
INSERT INTO `creature_ai_texts` VALUES (-418, 'Sir, our customers are complaining that there\'s too much Deathweed in our Anesthetics.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28355');
INSERT INTO `creature_ai_texts` VALUES (-419, 'Sir, I think we were close with the Lethargy Root in that last poison recipie.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28355');
INSERT INTO `creature_ai_texts` VALUES (-420, 'Then put twice as much in next time!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28347');
INSERT INTO `creature_ai_texts` VALUES (-421, 'Only put half as much in next time.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28347');
INSERT INTO `creature_ai_texts` VALUES (-422, 'Remember, kid: You\'re always more wrong than you think you are. Double It!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28347');
INSERT INTO `creature_ai_texts` VALUES (-423, 'Think I\'m starting to wear a rut in the paving stones.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3520');
INSERT INTO `creature_ai_texts` VALUES (-424, 'Jack and Jill my wrinkled patoot! I do all the water luggin\' \'round here.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3520');
INSERT INTO `creature_ai_texts` VALUES (-425, 'Seems like a hundred times a day I walk all the way to get more water. No respect for their elders I tell ya.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3520');
INSERT INTO `creature_ai_texts` VALUES (-426, 'Where\'s the water Emma? Get the water Emma? If\'n it weren\'t fer me that lot wouldn\'t know what water looks like.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3520');
INSERT INTO `creature_ai_texts` VALUES (-427, 'As if I don\'t have better things to do in my old age than carry buckets of water.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3520');
INSERT INTO `creature_ai_texts` VALUES (-428, 'O\'ourse I\'m talking to myself. Only way to get a decent conversation in this city.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3520');
INSERT INTO `creature_ai_texts` VALUES (-429, 'Are we there yet?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3504');
INSERT INTO `creature_ai_texts` VALUES (-430, 'My feet hurt.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3504');
INSERT INTO `creature_ai_texts` VALUES (-431, 'Is it true? Are there crocolisks in the canal?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3504');
INSERT INTO `creature_ai_texts` VALUES (-432, 'Why do we always go the same way?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3504');
INSERT INTO `creature_ai_texts` VALUES (-433, 'Where we goin\'?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3504');
INSERT INTO `creature_ai_texts` VALUES (-434, 'I wanna see the Mage Tower.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3504');
