INSERT INTO `creature_ai_texts` VALUES (-436, 'Looks like you\'ll have to go ahead to Ratchet and tell Sputtervalve that I\'ve wrecked the shredder.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3451');
INSERT INTO `creature_ai_texts` VALUES (-437, 'I\'ll stay behind and guard the wreck. Hurry! Hopefully no one will notice the smoke...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3451');
INSERT INTO `creature_ai_texts` VALUES (-438, 'We are betrayed!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '10433');
