INSERT INTO `creature_ai_texts` VALUES (-448, '%s lets out a shrill cry.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '10506');
INSERT INTO `creature_ai_texts` VALUES (-449, 'Who is this mere $R that meddles with that which is past?  May the legend of Stalvan die along with you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2044');
INSERT INTO `creature_ai_texts` VALUES (-450, 'Toil not in matters of the past, $N!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2044');
INSERT INTO `creature_ai_texts` VALUES (-451, 'Why haven\'t the Stormwind guards come?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '325');
INSERT INTO `creature_ai_texts` VALUES (-452, 'The light appears to have forsaken us.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '325');
INSERT INTO `creature_ai_texts` VALUES (-453, 'The news from Stormwind does not bode well. . . .', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '271');
INSERT INTO `creature_ai_texts` VALUES (-454, 'Constant bickering will get us nowhere.  We need to take action.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '269');
INSERT INTO `creature_ai_texts` VALUES (-455, 'We need better representation from Stormwind. Our homes are falling to the undead.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '270');
INSERT INTO `creature_ai_texts` VALUES (-456, 'I fear something dark is coming.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '325');
INSERT INTO `creature_ai_texts` VALUES (-457, 'Our cause falls on deaf ears beyond the thick, stone walls of Stormwind.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '271');
INSERT INTO `creature_ai_texts` VALUES (-458, 'The people of Darkshire expect more from the Council.  We cannot let them suffer from this unholy wrath which plagues us.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '269');
INSERT INTO `creature_ai_texts` VALUES (-459, 'Undead are crawling all over the land.  Where is the Stormwind Army?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '263');
INSERT INTO `creature_ai_texts` VALUES (-460, 'Wait...you are not my husband.  But he must have sent you.  And you...look..delicious!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '314');
INSERT INTO `creature_ai_texts` VALUES (-461, 'Aber?  Is that you...?  Oh...I\'m so hungry, Aber!  SO HUNGRY!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '314');
INSERT INTO `creature_ai_texts` VALUES (-462, 'The Council of Duskwood must take action.  Evil lingers in the air.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '269');
INSERT INTO `creature_ai_texts` VALUES (-463, 'I will end this quickly...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10385, 1, 0, 0, '17826');
INSERT INTO `creature_ai_texts` VALUES (-464, 'We fight to the death!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10384, 1, 0, 0, '17826');
INSERT INTO `creature_ai_texts` VALUES (-465, 'Beast! Obey me! Kill them at once!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10383, 1, 0, 0, '17826');
INSERT INTO `creature_ai_texts` VALUES (-466, 'Well... done...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10389, 1, 0, 0, '17826');
INSERT INTO `creature_ai_texts` VALUES (-467, 'Krypta!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10387, 1, 0, 0, '17826');
INSERT INTO `creature_ai_texts` VALUES (-468, 'It is finished.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 10388, 1, 0, 0, '17826');
INSERT INTO `creature_ai_texts` VALUES (-469, 'You will never stop the Forsaken, $R. The Dark Lady shall make you suffer.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2733');
INSERT INTO `creature_ai_texts` VALUES (-470, 'You have my word that I shall find a use for your body after I\'ve killed you, $R.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2733');
INSERT INTO `creature_ai_texts` VALUES (-471, '%s begins to crack and break apart.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '2359');
