INSERT INTO `creature_ai_texts` VALUES (-473, '%s is reduced to rubble, but still continues to fight.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '2359');
INSERT INTO `creature_ai_texts` VALUES (-474, 'Filthy $R intruder. DIE!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28575');
INSERT INTO `creature_ai_texts` VALUES (-475, 'If Rhunok regains his strength, we all die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28575');
INSERT INTO `creature_ai_texts` VALUES (-476, 'Daddy!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28917');
INSERT INTO `creature_ai_texts` VALUES (-477, 'Mmm, more mojo.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-478, 'NOOOOOOOOooooooo.........!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-479, 'I see you, insect! Come closer', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-480, 'With the power of the wild gods burning in our veins, none, not even the Lich King, can stand in our way!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-481, 'They dare attack us? The fools! Do they not know we are now all-powerful?!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-482, 'Finish them and then we will move south to kill their brethren!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-483, 'I will destroy the Argent Crusade! I will crush the Scourge!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-484, 'Kill these invaders who dare to defile our altar!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-485, 'Drink deep my brothers!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-486, 'The mojo of Sseratus is ours. Now all will be crushed under the might of the Drakkari!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '28068');
INSERT INTO `creature_ai_texts` VALUES (-487, 'WAIT... NO MORE!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25418');
INSERT INTO `creature_ai_texts` VALUES (-488, 'I... submit. As long as Simmer agrees to stop boiling my pool, I agree to an armistice.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25418');
INSERT INTO `creature_ai_texts` VALUES (-489, 'Now, remove yourself from my presence. You would be wise not to come within sight of me again.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25418');
INSERT INTO `creature_ai_texts` VALUES (-490, 'My back is killing me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '12337');
INSERT INTO `creature_ai_texts` VALUES (-491, 'If I should fall, take my pouch. Make certain that it gets to the High General.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '12337');
INSERT INTO `creature_ai_texts` VALUES (-492, 'I sense danger up ahead.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '12337');
INSERT INTO `creature_ai_texts` VALUES (-493, 'Death will be only the beginning of your torture.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '26165');
INSERT INTO `creature_ai_texts` VALUES (-494, 'My talons will shred your puny body, $R.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3277');
INSERT INTO `creature_ai_texts` VALUES (-495, 'I am the Keymaster. Are you the Gatekeeper?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25210');
INSERT INTO `creature_ai_texts` VALUES (-496, 'Your death will soon follow....', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25396');
INSERT INTO `creature_ai_texts` VALUES (-497, 'You cannot stop us all!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25396');
INSERT INTO `creature_ai_texts` VALUES (-498, 'Who dares?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25416');
INSERT INTO `creature_ai_texts` VALUES (-499, 'STOP!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25416');
INSERT INTO `creature_ai_texts` VALUES (-500, 'Tell Imperean that I will consider a cessation of hostilities. But first, Churn must stop his watery intrusions upon my rise!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25416');
INSERT INTO `creature_ai_texts` VALUES (-501, 'I grow bored with you. Begone!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25416');
INSERT INTO `creature_ai_texts` VALUES (-502, 'I presume you come with good news?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2597');
INSERT INTO `creature_ai_texts` VALUES (-503, 'Things are going as planned, Lord Falconcrest. I assure you, it is only a matter of time.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '2599');
INSERT INTO `creature_ai_texts` VALUES (-504, '...Go or I taunt you a second time!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28095');
INSERT INTO `creature_ai_texts` VALUES (-505, 'These prisons are to be filled at once or Malygos will know why!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25584');
INSERT INTO `creature_ai_texts` VALUES (-506, 'Bring me more prisoners! Do not rest until Azeroth has been cleansed of its reckless magic-users!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25584');
INSERT INTO `creature_ai_texts` VALUES (-507, 'What is the meaning of this disturbance?!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25584');
INSERT INTO `creature_ai_texts` VALUES (-508, 'Who dares interrupt my work!? Show yourself, coward!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25584');
INSERT INTO `creature_ai_texts` VALUES (-509, 'Ahh! It\'s coming right for us!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28095');
INSERT INTO `creature_ai_texts` VALUES (-510, 'Kaganishu!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Text');
INSERT INTO `creature_ai_texts` VALUES (-511, 'Etu Magmothregar, cha!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Text');
INSERT INTO `creature_ai_texts` VALUES (-512, 'Kada nu batteka, oht!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Common Text');
INSERT INTO `creature_ai_texts` VALUES (-513, 'You seek to deafen me? Pathetic... Wait... What\'s that sound?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25804');
INSERT INTO `creature_ai_texts` VALUES (-514, 'I\'m sure ol\' Hemet can do something with your bones!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25979');
INSERT INTO `creature_ai_texts` VALUES (-515, 'KILL KILL KILL!!!!!!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25979');
INSERT INTO `creature_ai_texts` VALUES (-516, 'Now THIS is a sport!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25979');
INSERT INTO `creature_ai_texts` VALUES (-517, 'No way! Rhino head helmet for the low, low price of thirty rhino heads? I\'ll take two at those prices!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25979');
INSERT INTO `creature_ai_texts` VALUES (-518, 'Just fifty more hooves and I\'ll have the new gun!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25979');
INSERT INTO `creature_ai_texts` VALUES (-519, 'Give it up, little beast!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25979');
INSERT INTO `creature_ai_texts` VALUES (-520, 'Nesingwary is going to go broke if he keeps this up! Lane probably has no idea what he\'s doing!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25979');
INSERT INTO `creature_ai_texts` VALUES (-521, 'It\'s gonna be veal tonight!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25979');
