INSERT INTO `creature_ai_texts` VALUES (-527, 'The Great One will smash you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Drywhisker');
INSERT INTO `creature_ai_texts` VALUES (-528, 'Die!  You will not free Myzrael!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Drywhisker');
INSERT INTO `creature_ai_texts` VALUES (-529, 'Stand firm, brothers.  And don\'t worry!  Size is on our side!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Drywhisker');
INSERT INTO `creature_ai_texts` VALUES (-530, 'No!  Leave us!  We must not fail our task!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Drywhisker');
INSERT INTO `creature_ai_texts` VALUES (-531, 'Take that!  The Drywhiskers will prevail!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Drywhisker');
INSERT INTO `creature_ai_texts` VALUES (-532, 'Stop!  Foolish $C, we cannot let you summon the creature Myzrael!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '2763');
INSERT INTO `creature_ai_texts` VALUES (-533, 'We are Mechagnome...resistence is futile.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25814');
INSERT INTO `creature_ai_texts` VALUES (-534, 'The flesh is weak. We will make you better, stronger...faster.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25814');
INSERT INTO `creature_ai_texts` VALUES (-535, 'we can decurse you, we have the technology', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25814');
INSERT INTO `creature_ai_texts` VALUES (-536, 'Warning! Warning! Intruder alert! Intruder alert!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25814');
INSERT INTO `creature_ai_texts` VALUES (-537, 'You have been detected. You will be assimilated or eliminated.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25814');
