INSERT INTO `creature_ai_texts` VALUES (-539, '%s is getting hungry!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '8567');
INSERT INTO `creature_ai_texts` VALUES (-540, '%s is getting VERY hungry!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '8567');
INSERT INTO `creature_ai_texts` VALUES (-541, 'For Silvermoon! For Sunstrider!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '17591');
INSERT INTO `creature_ai_texts` VALUES (-542, 'Your death will make a fitting tribute to the King!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '17591');
