INSERT INTO `creature_ai_texts` VALUES (-550, '%s goes into a rage after seeing a friend fall in battle!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '2287');
INSERT INTO `creature_ai_texts` VALUES (-551, '%s calls for help!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '2287');
INSERT INTO `creature_ai_texts` VALUES (-552, 'Greetings citizen.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, '466');
INSERT INTO `creature_ai_texts` VALUES (-553, 'Who dares disrupt the sanctity of Eldre\'Thalas? Face me, cowards!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '11486');
INSERT INTO `creature_ai_texts` VALUES (-554, 'Finally, my soul may rest... Oh, dearest Cerellean...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3667');
INSERT INTO `creature_ai_texts` VALUES (-555, 'I\'ll tell you everything! STOP! PLEASE!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-556, 'We... We have only been told that the \"Crimson Dawn\" is an awakening. You see, the Light speaks to the High General. It is the Light...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-557, 'The Light that guides us. The movement was set in motion before you came... We... We do as we are told. It is what must be done.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-558, 'I know very little else... The High General chooses who may go and who must stay behind. There\'s nothing else... You must believe me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-559, 'NO! PLEASE! There is one more thing that I forgot to mention... A courier comes soon... From Hearthglen. It...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-560, 'You\'ll be hanging in the gallows shortly, Scourge fiend!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-561, 'You\'ll have to kill me, monster! I will tell you NOTHING!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-562, 'You hit like a girl. Honestly. Is that the best you can do?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-563, 'ARGH! You burned my last good tabard!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-564, 'Argh... The pain... The pain is almost as unbearable as the lashings I received in grammar school when I was but a child.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-565, 'I used to work for Grand Inquisitor Isillien! Your idea of pain is a normal mid-afternoon for me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '28610');
INSERT INTO `creature_ai_texts` VALUES (-566, 'Who dares disturb my meditation!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5802, 1, 0, 0, '4832');
INSERT INTO `creature_ai_texts` VALUES (-567, 'Sleep...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5804, 1, 0, 0, '4832');
INSERT INTO `creature_ai_texts` VALUES (-568, 'Just... Dust...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5803, 1, 0, 0, '4832');
INSERT INTO `creature_ai_texts` VALUES (-569, 'Aku\'mai is dead! At last, I can leave this wretched place!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '6729');
INSERT INTO `creature_ai_texts` VALUES (-570, 'Cry for mercy! Your meaningless lives will soon be forfeit!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11015, 1, 0, 0, '17888');
INSERT INTO `creature_ai_texts` VALUES (-571, 'Your death will be a painful one.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11016, 1, 0, 0, '17888');
INSERT INTO `creature_ai_texts` VALUES (-572, 'You... are marked.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11052, 1, 0, 0, '17888');
INSERT INTO `creature_ai_texts` VALUES (-573, 'You... are nothing!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11053, 1, 0, 0, '17888');
INSERT INTO `creature_ai_texts` VALUES (-574, 'Miserable nuisance!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11054, 1, 0, 0, '17888');
INSERT INTO `creature_ai_texts` VALUES (-575, 'Shaza-Kiel!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11017, 1, 0, 0, '17888');
INSERT INTO `creature_ai_texts` VALUES (-576, 'By Thorazan\'s Beard, Slay Them!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5852, 1, 0, 0, '7291');
INSERT INTO `creature_ai_texts` VALUES (-577, 'Me Grimlok, King!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5853, 1, 0, 0, '4854');
INSERT INTO `creature_ai_texts` VALUES (-578, 'Die! Die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 5854, 1, 0, 0, '4854');
INSERT INTO `creature_ai_texts` VALUES (-579, '%s will be armed in 10 seconds!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '8035');
INSERT INTO `creature_ai_texts` VALUES (-580, '%s will be armed in 5 seconds!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '8035');
INSERT INTO `creature_ai_texts` VALUES (-581, '%s is now armed!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '8035');
INSERT INTO `creature_ai_texts` VALUES (-582, 'Bijou knows where treasures are hidden.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '10262');
INSERT INTO `creature_ai_texts` VALUES (-583, 'SQUAWK!! *cough* *cough* I need to quit smoking!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '10262');
INSERT INTO `creature_ai_texts` VALUES (-584, 'Ask Kibler about the treasure! SQUAWK!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '10262');
INSERT INTO `creature_ai_texts` VALUES (-585, 'They all go to the Spire but none never come back... just like Bijou.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '10262');
INSERT INTO `creature_ai_texts` VALUES (-586, 'Opus wants some Cuergo\'s Gold... with worm.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '10262');
INSERT INTO `creature_ai_texts` VALUES (-587, 'Kibler is being investigated by G.E.T.A.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '10262');
INSERT INTO `creature_ai_texts` VALUES (-588, 'When do we eat again?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18598');
INSERT INTO `creature_ai_texts` VALUES (-589, 'Don\'t feel.....so good.....', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18598');
INSERT INTO `creature_ai_texts` VALUES (-590, 'Hal...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-591, 'Yea, Nat?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20342');
INSERT INTO `creature_ai_texts` VALUES (-592, 'Had that dream again last night...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-593, 'Which one?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20342');
INSERT INTO `creature_ai_texts` VALUES (-594, 'That one dream... The crazy one. Remember?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-595, 'Tarren Mill is destroyed by some crazy force of undead and forever more becomes an enemy to Southshore.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-596, 'Could ya imagine such a thing, Hal? Could ya?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-597, 'Impossible!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20342');
INSERT INTO `creature_ai_texts` VALUES (-598, 'That one where thousands of people are all watching us up from the sky...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-599, 'We\'re like their puppets - we dance and cry and fight and say silly things for their amusement.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-600, 'Sometimes, even when I\'m awake, I think they\'re watching us. Maybe they\'re watching us right now...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-601, 'Nat, I\'ve heard a whopper or two in my day but that one takes the cake.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20342');
INSERT INTO `creature_ai_texts` VALUES (-602, 'So I\'m on some mountain with some big ol\' tree. Bunch of elves runnin\' around all over...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-603, 'Out of nowhere, some 300 foot tall demon or somethin\' walks up and starts climbin\' the tree...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-604, 'And if that weren\'t crazy enough, that big demon gets killed by a bunch of floating light things... Oh, I was also a female elf in that one... Yep...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-605, 'In this dream, I was fishin\' master of the world. I moved to some place called Kalimdor...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-606, 'And people from all over the world come to me to pay homage... and learn to fish.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-607, 'I\'m like some kind of fishing god...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-608, 'This one\'s real grim... So the king\'s kid Arthas...Well he goes out to battle evil, along with Uther *Nat pats his brow dry*', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-609, 'But tragically, Arthas is consumed by the evil and becomes evil himself...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-610, 'Well, he comes back to the king all pretendin\' to be nice, draws his sword, and runs the king through, elbow to... well you know... kills him on the spot...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-611, 'I can\'t even believe this one... You know two loudmouthed ruffians, Foror and Tigule?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-612, 'Well in this dream, they somehow end up inventing something called ice-cream and flavoring it with strawberries... Well, long story short, they end up striking it rich!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-613, 'If that weren\'t crazy enough, they decided to quit the ice-cream business and become adventurers... They travel all over the place and finally disappears into some portal. I woke up in a cold sweat after that one...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-614, 'That one where i\'m in Durnholde and that one orc that Blackmoore keeps as his personal slave breaks out...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-615, 'He ends up destroying the whole keep, freeing all the orcs we\'re keeping in the camps and rising to power as the king of orcs... Warchief or somethin\'.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-616, 'Crazy isn\'t it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20344');
INSERT INTO `creature_ai_texts` VALUES (-617, 'No way!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20342');
INSERT INTO `creature_ai_texts` VALUES (-618, 'It\'ll never happen, Nat.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20342');
INSERT INTO `creature_ai_texts` VALUES (-619, 'What do you put in that pipe of yours Nat?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20342');
INSERT INTO `creature_ai_texts` VALUES (-620, 'Keep your voice down, Helcular. Strangers abound...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20350');
INSERT INTO `creature_ai_texts` VALUES (-621, 'Necromancy. It is called necromancy. And yes, I have it within my power to bless you with this gift.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20350');
INSERT INTO `creature_ai_texts` VALUES (-622, 'That is none of your concern, Helcular, as you are neither Kirin Tor nor a necromancer.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20350');
INSERT INTO `creature_ai_texts` VALUES (-623, 'But to be perfectly frank, I do not give a damn what the Kirin Tor think! They are fools, set in their archaic ways.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20350');
INSERT INTO `creature_ai_texts` VALUES (-624, 'In due time, Helcular... All in due time...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20350');
INSERT INTO `creature_ai_texts` VALUES (-625, 'So can you teach me this... this...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20353');
INSERT INTO `creature_ai_texts` VALUES (-626, 'And the Kirin Tor? What have they to say of this necromancy?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20353');
INSERT INTO `creature_ai_texts` VALUES (-627, 'Then teach me, Kel\'Thuzad. Teach me everything you know...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '20353');
INSERT INTO `creature_ai_texts` VALUES (-628, 'What? Where in a..? Don\'t just stand around lads KILL SOMEBODY!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13193, 1, 0, 0, '26796');
INSERT INTO `creature_ai_texts` VALUES (-629, 'Now we\re getting someplace!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13195, 1, 0, 0, '26796');
INSERT INTO `creature_ai_texts` VALUES (-630, 'Is that all you\'ve... got?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13194, 1, 0, 0, '26796');
INSERT INTO `creature_ai_texts` VALUES (-631, 'What is this? Mag thorin Kar! Kill them!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13458, 1, 0, 0, '26798');
INSERT INTO `creature_ai_texts` VALUES (-632, 'Our task is not yet done!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13459, 1, 0, 0, '26798');
INSERT INTO `creature_ai_texts` VALUES (-633, 'GOOO!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13460, 1, 0, 0, '26798');
INSERT INTO `creature_ai_texts` VALUES (-634, 'This kingdom belongs to the Scourge! Only the dead may enter.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14075, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-635, 'They hunger.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14085, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-636, 'Dinner time, my pets.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14086, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-637, 'As Anub\'Arak commands!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14078, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-638, 'Hash\'tak hi\'tak.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14076, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-639, 'You were foolish to come.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14077, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-640, 'Keep an eye on the tunnel. We must not let anyone through!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14082, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-641, 'I hear footsteps. Be on your guard.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14083, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-642, 'I sense the living. Be ready.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14084, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-643, 'I should be grateful. But I long ago lost the capacity.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14087, 1, 0, 0, '28684');
INSERT INTO `creature_ai_texts` VALUES (-644, 'Eternal aggony awaits you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14054, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-645, 'Your armor is useless againts my locusts.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14060, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-646, 'Uunak-hissss tik-k-k-k-k!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14067, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-647, 'The pestilence upon you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14068, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-648, 'You shall experience my torment, first-hand!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14055, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-649, 'You have made your choice.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14056, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-650, 'Soon, the Master\'s voice will call to you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14057, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-651, 'Auum na-l ak-k-k-k, isshhh.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14058, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-652, 'Come forth my brethren! Feast on their flesh.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14059, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-653, 'Ahhh... RAAAAAGH! Never thought... I would be free of him...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14069, 1, 0, 0, '29120');
INSERT INTO `creature_ai_texts` VALUES (-654, 'Snivvle is here! Snivvle claims the Coldtooth Mine!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '11677');
INSERT INTO `creature_ai_texts` VALUES (-655, 'Tell us!  Where does your leader hide?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16924');
INSERT INTO `creature_ai_texts` VALUES (-656, 'Please!  I must feed on something soon... A mana crystal.... a shard... anything!  The pain is unbearable!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16916');
INSERT INTO `creature_ai_texts` VALUES (-657, 'You\'re not getting anything until you speak, scum!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16924');
INSERT INTO `creature_ai_texts` VALUES (-658, 'The numbers of my companions dwindles, goddess, and my own power shall soon be insufficient to hold back the demons of Felwood.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3946');
INSERT INTO `creature_ai_texts` VALUES (-659, 'Goddess, grant me the power to overcome my enemies! Hear me, please, my need is desperate and my cause is just!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3946');
INSERT INTO `creature_ai_texts` VALUES (-660, 'What... what is this? Could this be the answer to my prayers? Elune has granted me a weapon--this scythe--to defeat the demons.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3946');
INSERT INTO `creature_ai_texts` VALUES (-661, 'Anaya...? Do my eyes deceive me? Is it really you?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644');
INSERT INTO `creature_ai_texts` VALUES (-662, 'That fates should be so cruel as to permit us only this after a thousand years apart...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644');
INSERT INTO `creature_ai_texts` VALUES (-663, 'Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644');
INSERT INTO `creature_ai_texts` VALUES (-664, 'No! Anaya... Anaya! Don\'t leave me! Please...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644');
INSERT INTO `creature_ai_texts` VALUES (-665, 'How, my love? How will I find the strength to face the ages of the world without you by my side...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3644');
INSERT INTO `creature_ai_texts` VALUES (-666, 'The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3843');
INSERT INTO `creature_ai_texts` VALUES (-667, 'Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3843');
INSERT INTO `creature_ai_texts` VALUES (-668, 'Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3843');
INSERT INTO `creature_ai_texts` VALUES (-669, 'Farewell, Cerellean, until we are joined once again...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '3843');
INSERT INTO `creature_ai_texts` VALUES (-670, 'Anaya\'s soft voice trails away into the mists, \"Know that I love you always...\"', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '3843');
INSERT INTO `creature_ai_texts` VALUES (-671, 'Incompetent fools! Auchindoun should\'ve been ours... look at yourselves scrambling like rats!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '22381');
INSERT INTO `creature_ai_texts` VALUES (-672, 'This world is OURS!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'Common Orc Say');
INSERT INTO `creature_ai_texts` VALUES (-673, 'You there!  Keep a close watch on these ramparts, intruders could approach at any time!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '17264');
INSERT INTO `creature_ai_texts` VALUES (-674, 'For Kargath!  For Victory!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '17264');
INSERT INTO `creature_ai_texts` VALUES (-675, 'How can you just leave me here?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18206');
INSERT INTO `creature_ai_texts` VALUES (-676, 'Help me!  Please help me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18206');
INSERT INTO `creature_ai_texts` VALUES (-677, 'Will the pain ever end?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18206');
INSERT INTO `creature_ai_texts` VALUES (-678, 'Assist me slaves!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '17959');
INSERT INTO `creature_ai_texts` VALUES (-679, 'Hurry up with it already! The longer you take, the more of a hurtin\' I\'m putting on you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '17959');
INSERT INTO `creature_ai_texts` VALUES (-680, 'Wake up! Now get up and back to work!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '17959');
INSERT INTO `creature_ai_texts` VALUES (-681, 'Too soon! You are slacking off too soon!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '17959');
INSERT INTO `creature_ai_texts` VALUES (-682, 'Hey!  Over here!  Yeah, over here... I\'m in this cage!!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '17959');
INSERT INTO `creature_ai_texts` VALUES (-683, 'This is terrible..... my arms grow tired from beating on you lazy peons!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '17959');
INSERT INTO `creature_ai_texts` VALUES (-684, 'Get back to work you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '17959');
INSERT INTO `creature_ai_texts` VALUES (-685, 'What is this?! Didn\'t mommy and daddy teach you anything?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '17959');
INSERT INTO `creature_ai_texts` VALUES (-686, '%s begins a massive heal!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '17730');
INSERT INTO `creature_ai_texts` VALUES (-687, 'Who dares slay one of my Kargath\'s commanders?  You will pay for this... in flesh!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '22374');
INSERT INTO `creature_ai_texts` VALUES (-688, 'Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13862, 1, 0, 0, '26632');
INSERT INTO `creature_ai_texts` VALUES (-689, 'Im... impossible! Tharon\'ja is eternal! Tharon\'ja... is...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13869, 1, 0, 0, '26632');
INSERT INTO `creature_ai_texts` VALUES (-690, 'As Tharon\'ja predicted.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13863, 1, 0, 0, '26632');
INSERT INTO `creature_ai_texts` VALUES (-691, 'As it was written.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13864, 1, 0, 0, '26632');
INSERT INTO `creature_ai_texts` VALUES (-692, 'Your flesh serves Tharon\'ja now!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13865, 1, 0, 0, '26632');
INSERT INTO `creature_ai_texts` VALUES (-693, 'Tharon\'ja has a use for your mortal shell!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13866, 1, 0, 0, '26632');
INSERT INTO `creature_ai_texts` VALUES (-694, 'No! A taste... all too brief!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13867, 1, 0, 0, '26632');
INSERT INTO `creature_ai_texts` VALUES (-695, 'Tharon\'ja will have more!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13868, 1, 0, 0, '26632');
INSERT INTO `creature_ai_texts` VALUES (-696, 'King Dred raises his talon menacingly!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 5, 0, 0, '27483');
INSERT INTO `creature_ai_texts` VALUES (-697, 'King Dred calls for a raptor!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 5, 0, 0, '27483');
