INSERT INTO `creature_ai_texts` VALUES (-727, 'As disciples of blood, you strive to master the very lifeforce of your enemies.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29196');
INSERT INTO `creature_ai_texts` VALUES (-728, 'Be it by blade or incantation, blood feeds our attacks and weakens our foes.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29196');
INSERT INTO `creature_ai_texts` VALUES (-729, 'True masters learn to make blood serve more than just their strength in battle.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29196');
INSERT INTO `creature_ai_texts` VALUES (-730, 'Stripping energy from our foes, both fighting and fallen, allows us to persevere where lesser beigns falls exhausted.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29196');
INSERT INTO `creature_ai_texts` VALUES (-731, 'And every foe that falls, energy sapped and stolen, only further fuels our assault.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29196');
