INSERT INTO `creature_ai_texts` VALUES (-832, 'It\'s coming along, Wafflefry. Haven\'t you got a product demonstration to do or something?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '19475');
INSERT INTO `creature_ai_texts` VALUES (-833, 'Be the first on your block to own the incredible Electrostatic Maculation Blaster!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '25885');
INSERT INTO `creature_ai_texts` VALUES (-834, 'Derived from cutting edge Shattered Sun Offensive military technology, it blasts away stubborn stains with ease!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '25885');
INSERT INTO `creature_ai_texts` VALUES (-835, 'Wine stains, grass stains, even pet stains are no match for the Electrostatic Maculation Blaster!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '25885');
INSERT INTO `creature_ai_texts` VALUES (-836, 'Don\'t wait! Order now and receive my Ultra-Kinetic Waffle Iron at no additional charge!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '25885');
INSERT INTO `creature_ai_texts` VALUES (-837, 'Hey man, got some money? I\'ve got five kids to feed!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '19283');
INSERT INTO `creature_ai_texts` VALUES (-838, 'Spare a copper, madame?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '19283');
INSERT INTO `creature_ai_texts` VALUES (-839, 'Please don\'t ignore us...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '19283');
INSERT INTO `creature_ai_texts` VALUES (-840, 'A cool breeze caresses your face. Gentle, melodic humming resonates throughout the chamber.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '19412');
INSERT INTO `creature_ai_texts` VALUES (-841, 'Play time!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13428, 1, 0, 0, '26529');
INSERT INTO `creature_ai_texts` VALUES (-842, 'New toys!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13429, 1, 0, 0, '26529');
INSERT INTO `creature_ai_texts` VALUES (-843, 'Boring...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13430, 1, 0, 0, '26529');
INSERT INTO `creature_ai_texts` VALUES (-844, 'Why you stop moving?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13431, 1, 0, 0, '26529');
INSERT INTO `creature_ai_texts` VALUES (-845, 'Get up! Me not done!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13432, 1, 0, 0, '26529');
INSERT INTO `creature_ai_texts` VALUES (-846, 'This not fun...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13433, 1, 0, 0, '26529');
INSERT INTO `creature_ai_texts` VALUES (-847, 'You are too late, champion of Lordaeron. The dead shall have their day.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13471, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-848, 'Ah, the entertainment has arrived!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13472, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-849, 'The fun is just beginning!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13473, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-850, 'Aah, quality materials!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13474, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-851, 'Don\'t worry, I\'ll make good use of you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13475, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-852, 'Say hello to some friends of mine.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13476, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-853, 'Come, citizen of Stratholme! Meet your saviors.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13477, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-854, 'BOOM! Hahahahah...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13478, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-855, 'Blood... destruction... EXHILARATING!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13479, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-856, 'I want a sample...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13480, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-857, 'Such strength... it must be mine!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13481, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-858, 'Your flesh betrays you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13482, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-859, 'You only advance... the master\'s plan...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13483, 1, 0, 0, '26530');
INSERT INTO `creature_ai_texts` VALUES (-860, 'Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13408, 6, 0, 0, '26532');
INSERT INTO `creature_ai_texts` VALUES (-861, 'We\'ll see about that, young prince.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13409, 1, 0, 0, '26532');
INSERT INTO `creature_ai_texts` VALUES (-862, 'Tick tock, tick tock...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13410, 1, 0, 0, '26532');
INSERT INTO `creature_ai_texts` VALUES (-863, 'Not quick enough!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13411, 1, 0, 0, '26532');
INSERT INTO `creature_ai_texts` VALUES (-864, 'Let\'s get this over with.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13412, 1, 0, 0, '26532');
INSERT INTO `creature_ai_texts` VALUES (-865, 'There is no future for you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13413, 1, 0, 0, '26532');
INSERT INTO `creature_ai_texts` VALUES (-866, 'This is the hour of our greatest triumph!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13414, 1, 0, 0, '26532');
INSERT INTO `creature_ai_texts` VALUES (-867, 'You would destined to fail.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 13415, 1, 0, 0, '26532');
INSERT INTO `creature_ai_texts` VALUES (-868, 'Yes, this is the beginning. I\'ve been waiting for you, young prince. I am Mal\'Ganis.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14410, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-869, 'As you can see, your people are now mine. I will now turn this city, household by household, until the flame of life has been snuffed out forever.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14411, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-870, 'Your journey has just begun, young prince. Gather your forces, and meet me in the arctic land of Northrend. It is there we shall settle the score between us. It is there that your true destiny will unfold.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14412, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-871, 'This will be a fine test, Prince Arthas.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14413, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-872, 'Time out.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14414, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-873, 'You seem tired.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14415, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-874, 'All too easy.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14416, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-875, 'The dark lord is displeased with your interference.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14417, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-876, 'It is Prince Arthas I want, not you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 14418, 1, 0, 0, '26533');
INSERT INTO `creature_ai_texts` VALUES (-877, 'Everyone is falling ill - this is an epidemic!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '30994');
INSERT INTO `creature_ai_texts` VALUES (-878, 'The soldiers are spreading panic with rumors of bad food. Neighbors are accusing one another of poison. The city will fall into bedlam!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '30994');
INSERT INTO `creature_ai_texts` VALUES (-879, 'No remedy seems to work - the entire city has sickened...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '30994');
INSERT INTO `creature_ai_texts` VALUES (-880, 'I\'m at a loss. What can one simple man do in the face of disaster?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '30994');
INSERT INTO `creature_ai_texts` VALUES (-881, 'How can I possibly help the city in such a widespread crisis?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '30994');
INSERT INTO `creature_ai_texts` VALUES (-882, 'I pray the illness I\'m feeling is due to stress ulcers...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '30994');
INSERT INTO `creature_ai_texts` VALUES (-883, 'Hello. Master Fras Siabi is not in at this time. Please come back tomorrow to Fras Siabi\'s Premium Tobacco! Beep!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '31057');
INSERT INTO `creature_ai_texts` VALUES (-884, 'I survived and I\'m flesh again!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, '25773');
INSERT INTO `creature_ai_texts` VALUES (-885, 'But... but... I was perfect!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, '25773');
INSERT INTO `creature_ai_texts` VALUES (-886, 'Where am I? Who are you? What\'s that strange feeling?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, '25773');
INSERT INTO `creature_ai_texts` VALUES (-887, 'I\'m flesh and blood again! Thank you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, '25773');
INSERT INTO `creature_ai_texts` VALUES (-888, 'I knew that you would come.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '31161');
INSERT INTO `creature_ai_texts` VALUES (-889, 'You have some fight in you. I\'m impressed.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '31161');
INSERT INTO `creature_ai_texts` VALUES (-890, 'Welcome to the world of the unliving!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '31161');
INSERT INTO `creature_ai_texts` VALUES (-891, 'Well fought...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '31161');
INSERT INTO `creature_ai_texts` VALUES (-892, 'I know why you\'ve come - one of those foolish Magnataur on the plains meddled and managed to get the dragons involved. Do you enjoy serving them like dogs?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '27003');
INSERT INTO `creature_ai_texts` VALUES (-893, 'You seek their leader... little things, you wage war against the clans of Grom\'thar the Thunderbringer himself. Don\'t be so eager to rush to your deaths.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '27003');
INSERT INTO `creature_ai_texts` VALUES (-894, 'Hah! So be it. Blow the horn of a magnataur leader at the ring of torches south of the Blue Dragonshrine. Make peace with your gods... Grom\'thar will come.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '27003');
INSERT INTO `creature_ai_texts` VALUES (-895, 'There is no escape for you. The Crusade shall destroy all who carry the scourge\'s taint.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1535');
INSERT INTO `creature_ai_texts` VALUES (-896, 'You carry the taint of the scourge. Prepare to enter the twisting nether.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1535');
INSERT INTO `creature_ai_texts` VALUES (-897, 'The light condemns all who harbor evil. Now you will die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '1535');
INSERT INTO `creature_ai_texts` VALUES (-898, 'Do not allow Kaskala to forget what has happened here.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25321');
INSERT INTO `creature_ai_texts` VALUES (-899, 'Thank you for freeing me! May the tides always favor you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25321');
INSERT INTO `creature_ai_texts` VALUES (-900, 'Thank you, $C. May the winds and seas always deliver you safely.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25322');
INSERT INTO `creature_ai_texts` VALUES (-901, 'May the ancestors always aid you, $C, as you have aided me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, '25322');
INSERT INTO `creature_ai_texts` VALUES (-902, 'What a pity you survived the crash just to die here.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16521');
INSERT INTO `creature_ai_texts` VALUES (-903, 'We won\'t allow you to leave this valley!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16521');
INSERT INTO `creature_ai_texts` VALUES (-904, 'I hope you\'ve made your peace.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16521');
INSERT INTO `creature_ai_texts` VALUES (-905, 'You\'ll not take me alive, scum!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '16522');
INSERT INTO `creature_ai_texts` VALUES (-906, 'The Inoculated Nestlewood Owlkin didn\'t like what just happened.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 18, '16534');
INSERT INTO `creature_ai_texts` VALUES (-907, 'The Inoculated Nestlewood Owlkin looks confused.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 6, '16534');
INSERT INTO `creature_ai_texts` VALUES (-908, 'The Inoculated Nestlewood Owlkin nods appreciatively.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 2, '16534');
INSERT INTO `creature_ai_texts` VALUES (-909, 'The Inoculated Nestlewood Owlkin doen\'t look like it minds the crystal\'s effect.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 22, '16534');
INSERT INTO `creature_ai_texts` VALUES (-910, 'The Inoculated Nestlewood Owlkin seems overjoyed.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 4, '16534');
INSERT INTO `creature_ai_texts` VALUES (-911, '<Sobbing> I hate graveyards. So many dead. And my sweet Luhanaa.....', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '17073');
INSERT INTO `creature_ai_texts` VALUES (-912, 'It should have been me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '17073');
INSERT INTO `creature_ai_texts` VALUES (-913, 'Be at peace, my sweet, sweet, Luhanaa.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '17073');
INSERT INTO `creature_ai_texts` VALUES (-914, 'I\'ll never forget you, my love.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '17073');
INSERT INTO `creature_ai_texts` VALUES (-915, 'If I ever find the ones responsible for the crash...!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '17073');
INSERT INTO `creature_ai_texts` VALUES (-916, 'We should never have come along.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '17073');
INSERT INTO `creature_ai_texts` VALUES (-917, 'Why, Luhanaa? Why?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 6, '17073');
INSERT INTO `creature_ai_texts` VALUES (-918, 'I miss you so much!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '17073');
INSERT INTO `creature_ai_texts` VALUES (-919, 'You\'ll always be here, with me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '17073');
INSERT INTO `creature_ai_texts` VALUES (-920, 'Draenei Mourner weeps softly.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 18, '17073');
INSERT INTO `creature_ai_texts` VALUES (-921, 'Draenei Mourner bows his head and sighs, clearly exhausted.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 2, '17073');
INSERT INTO `creature_ai_texts` VALUES (-922, 'Draenei Mourner breaks down into huge, wracking sobs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 18, '17073');
INSERT INTO `creature_ai_texts` VALUES (-923, 'Draenei Mourner stares in silence at the graver marker before him.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '17073');
INSERT INTO `creature_ai_texts` VALUES (-924, 'More agents of the Syndicate I\'ll wager! You\'ll never take back Alterac... Or Southshore!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 0, '2276');
INSERT INTO `creature_ai_texts` VALUES (-925, 'This orchard is now as barren and lifeless as my rotting shell. Once these trees bloomed with fruit but now there is only rot.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, '2396');
INSERT INTO `creature_ai_texts` VALUES (-926, 'I spent my whole life caring for these trees. Pruning and nurturing them... only fitting that they have joined me in death.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, '2396');
INSERT INTO `creature_ai_texts` VALUES (-927, 'Intruders! Attack the intruders!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 33, 0, '2405');
INSERT INTO `creature_ai_texts` VALUES (-928, 'Southshore shall pay in blood!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 1, '2433');
INSERT INTO `creature_ai_texts` VALUES (-929, 'I have risen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 1, '2433');
INSERT INTO `creature_ai_texts` VALUES (-930, 'Revenge shall be mine at last!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 1, '2433');
INSERT INTO `creature_ai_texts` VALUES (-931, 'The undead shall feast on your soul, $N.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 1, '2433');
INSERT INTO `creature_ai_texts` VALUES (-932, 'Help me! Help! Assassins in the hills! To arms! To arms!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 22, '2434');
INSERT INTO `creature_ai_texts` VALUES (-933, 'Guards to your posts! They must be after the Magistrate again. He must be protected! They could come from any direction!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '2434');
INSERT INTO `creature_ai_texts` VALUES (-934, '%s grabs the back of his neck and his eyes go wide.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, '2434');
INSERT INTO `creature_ai_texts` VALUES (-935, 'Assassins... save... th... Magistraaa...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 65, '2434');
INSERT INTO `creature_ai_texts` VALUES (-936, 'Off with you, $N.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 66, '15371');
INSERT INTO `creature_ai_texts` VALUES (-937, 'Move along, $C.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 66, '15371');
INSERT INTO `creature_ai_texts` VALUES (-938, 'Do not push it citizen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '15371');
INSERT INTO `creature_ai_texts` VALUES (-939, 'Does anyone need any supplies to down the trolls? Any troll will do.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '16263');
INSERT INTO `creature_ai_texts` VALUES (-940, 'I don\'t see why we ally with filthy stinking trolls? The only good troll is a dead troll.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '16263');
INSERT INTO `creature_ai_texts` VALUES (-941, 'If only I had some wine to go with my cheese...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 18, '18929');
INSERT INTO `creature_ai_texts` VALUES (-942, 'Why couldn\'t mother have chosen something else to dedicate herself to? Cakes, pastries, even bread - anything but cheese!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '18929');
INSERT INTO `creature_ai_texts` VALUES (-943, 'Ooh what a dreadful job! All day, surrounded by the stench of bad feet.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '18929');
INSERT INTO `creature_ai_texts` VALUES (-944, 'Was... Was it all a dream? Azuremyst, Bloodymyst, Zangarmarsh, Shadowmoon... even Zul\'Aman... Jane! Jane where are you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '20100');
INSERT INTO `creature_ai_texts` VALUES (-945, 'I\'m right here, daddy. You\'ve been asleep all day long. You sure must be tired!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '20098');
INSERT INTO `creature_ai_texts` VALUES (-946, 'Ah, wonderful. It was all just a dream...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '20100');
INSERT INTO `creature_ai_texts` VALUES (-947, 'The pain...the need...I hunger for more...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 10, 0, 'Common Wretched Text');
INSERT INTO `creature_ai_texts` VALUES (-948, 'What is this? I recognize you from when I killed Akali. You must be this $N that everyone is dreading.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29028');
INSERT INTO `creature_ai_texts` VALUES (-949, 'You dont worry me, boy. Just the opposite - I look forward to killing you and your friends. Nothing will stand between me and Zim\'Torga now!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29028');
INSERT INTO `creature_ai_texts` VALUES (-950, 'How sad that only now do you grasp the truth - I wield the power of a god. You are a mere mortal. You cannot hope to defeat me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29028');
INSERT INTO `creature_ai_texts` VALUES (-951, 'You\'ve meddled in affairs that were none of your business. You are nothing but foreign invaders. Prepare to die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29028');
INSERT INTO `creature_ai_texts` VALUES (-952, 'What? HARKOA?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29028');
INSERT INTO `creature_ai_texts` VALUES (-953, 'Very well, kitten. I see no need to delay your inevitable demise. I look forward to dealing with you just as soon as I finish off these simpletons!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29028');
INSERT INTO `creature_ai_texts` VALUES (-954, 'NO! I will kill you all!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29028');
INSERT INTO `creature_ai_texts` VALUES (-955, 'PROPHET!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29050');
INSERT INTO `creature_ai_texts` VALUES (-956, 'YOU KEEP YOUR FILTHY DRAKKARI HANDS OFF THEM!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29050');
INSERT INTO `creature_ai_texts` VALUES (-957, 'I THINK NOT. YOU WILL DROP YOUR IMMUNITY, CEASE YOUR RITUAL NOW AND RELEASE THEM!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29050');
INSERT INTO `creature_ai_texts` VALUES (-958, 'Thank you. Once again you have saved us all. I will see you back at Zim\'Torga.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '29050');
INSERT INTO `creature_ai_texts` VALUES (-959, 'Intruder detected! Neutralizing threat immediately!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '18698');
INSERT INTO `creature_ai_texts` VALUES (-960, 'I love the smell of nether in the morning.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '19635');
INSERT INTO `creature_ai_texts` VALUES (-961, 'It smells like...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, '19635');
INSERT INTO `creature_ai_texts` VALUES (-962, '...victory.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 15, '19635');
